									}
								}
							}
							else
							{
								Console.WriteLine(Language.GetTextValue("CLI.InvalidCommand"));
							}
						}
					}
				}
				catch
				{
					Console.WriteLine(Language.GetTextValue("CLI.InvalidCommand"));
				}
			}
		}

		public static void Sundialing()
		{
